import requests
import re
from bs4 import BeautifulSoup
import pandas as pd
url='http://music.163.com/discover/toplist?id=60198'
response=requests.get(url)
html=response.text
#==============================================================================
soup=BeautifulSoup(html,'lxml')
html_songs=soup.find_all('ul',attrs = {'class':'f-hide'}) #歌曲信息在这个html里
html_singers=soup.find_all('textarea',attrs = {'style':'display:none;'})[0]
#print(html_singers)
#==============================================================================
songs=re.findall('<li>.*?">(.*?)</a>',str(html_songs),re.S) #html是list,要转化为str
#print(songs)
#==============================================================================
singers=re.findall('"artists".*?"name":"(.*?)".*?,"tns"',str(html_singers),re.S)
#print(singers)
album=re.findall('"album".*?"name":"(.*?)","picUrl"',str(html_singers),re.S)
result=pd.DataFrame({"artist":singers,"song":songs,"album":album})
print(result)
#print(album)
#写进excel里
result.to_csv('billboard20170921.csv',index=None)



